mapper.update:
UPDATE user
   SET phone = #{phone}
      ,address = #{address}
      ,id = #{id}
      ,email = #{email}
      ,name = #{name}
      ,upd_date = now()
WHERE email = #{email}

UPDATE user_auth
   SET auth = #{auth}
WHERE user_id = #{email}

mapper.select:
SELECT COUNT(*)
FROM user
WHERE name = #{name} AND email = #{email}

SELECT COUNT(*)
FROM user
WHERE id = #{id}
